import java.io.*;import java.nio.*;class DNTParser {  public static void main(String args[]) throws IOException {    File file = new File("X:/resource/skilltreeadvicetable.dnt");    RandomAccessFile stream = new RandomAccessFile(file, "r");    stream.seek(4);    byte[] cols = new byte[2];    stream.read(cols);    System.out.println("Col Count: " + ByteBuffer.wrap(cols).order(ByteOrder.LITTLE_ENDIAN).getShort());    byte[] rows = new byte[4];    stream.read(rows);    System.out.println("Total Entries: " + ByteBuffer.wrap(rows).order(ByteOrder.LITTLE_ENDIAN).getInt());    stream.close();  }}